from flask import Flask, render_template, request
app = Flask(__name__)

temp_img_encoded = 'data:image/png;base64,'

# Global state for stats, bad for multithreading
app.cache = {}
app.cache['adversarial_enabled'] = False
app.cache['original_img'] = temp_img_encoded
app.cache['adv_mod_img'] = temp_img_encoded
app.cache['modified_img'] = temp_img_encoded

@app.route('/push_stats', methods=['POST'])
def push_stats():
    app.cache['adversarial_enabled'] = request.form['adversarial'] == 'yes'
    app.cache['original_img'] = request.form['original_img']
    app.cache['adv_mod_img'] = request.form['adv_mod_img']
    app.cache['modified_img'] = request.form['modified_img']
    return ('', 204)

@app.route('/')
def stats_main():
    return render_template("stats.html",
                           adv=app.cache['adversarial_enabled'],
                           original_img=app.cache['original_img'],
                           adv_mod_img=app.cache['adv_mod_img'],
                           modified_img=app.cache['modified_img'])
